const contactForm=document.getElementById("contactForm");function showModal(o,t){Swal.fire({html:`<div class="modal"><img style="width:100%;" src="assets/img/modal.png"><div>hello world - ${t}</div></div>`,showCloseButton:!0,showCancelButton:!1,showConfirmButton:!1})}contactForm.addEventListener("submit",function(o){o.preventDefault();o=new FormData(contactForm);fetch("sendEmail.php",{method:"POST",body:o}).then(function(o){o.ok?showModal("Success","Email sent successfully"):400===o.status?showModal("Error","Missing required fields"):showModal("Error","An error occurred while sending the email")}).catch(function(o){showModal("Error","An error occurred while sending the email")})});